// Hent dagens dato og formatér den til "DD-MM-YYYY"
let today = new Date();
let day = String(today.getDate()).padStart(2, '0');
let month = String(today.getMonth() + 1).padStart(2, '0');
let year = today.getFullYear();
let formattedToday = `${day}-${month}-${year}`;

// Gå igennem arrayet i msg.payload
let array = msg.payload;
let result = array.find(item => item.dato === formattedToday);

if (result) {
    // Konverter tal til tekst for dagen og formater dato i langt dansk format
    const months = [
        "januar", "februar", "marts", "april", "maj", "juni",
        "juli", "august", "september", "oktober", "november", "december"
    ];

    const dayNames = [
        "første", "anden", "tredje", "fjerde", "femte", "sjette", "syvende", "ottende", "niende", "tiende",
        "elfte", "tolvte", "trettende", "fjortende", "femtende", "sekstende", "syttende", "attende", "nittende", "tyvende",
        "enogtyvende", "toogtyvende", "treogtyvende", "fireogtyvende", "femogtyvende", "seksogtyvende", "syvogtyvende", "otteogtyvende", "niogtyvende", "tredivte", "enogtredivte"
    ];

    let [dayNum, monthNum] = result.dato.split('-');
    let dayText = dayNames[parseInt(dayNum) - 1]; // Konverter dag til tekst
    let monthText = months[parseInt(monthNum) - 1]; // Find månedsnavn

    let longDate = `${dayText} ${monthText}`;

    // Returner resultatet i msg.payload
    msg.payload = {
        dato: longDate,
        afsnit: result.afsnit,
        farjoke: result.farjoke
    };
} else {
    // Returner fejlmeddelelse hvis dato ikke matcher
    msg.payload = { error: "Øv... Jeg kunne ikke finde noget afsnit til i dag" };
}

return msg;

// Tjek om msg.payload indeholder de forventede felter
if (msg.payload.dato && msg.payload.afsnit && msg.payload.farjoke) {
    // Formatér "dato", "afsnit" og "farjoke" sammen til én streng
    msg.payload.message = `${msg.payload.dato}.. ${msg.payload.afsnit}... SLUT..... Kom. lad os grine lidt.. ${msg.payload.farjoke}`;
} else {
    // Hvis felterne ikke er til stede, returner ingenting
    msg.payload.message = "";
}

return msg;
